import 'package:flutter/material.dart';

class AboutPage extends StatelessWidget {
  const AboutPage({super.key});

  @override
  Widget build(BuildContext context) {
    return Scaffold(
      appBar: AppBar(
        title: const Text(
          "About Me",
          style: TextStyle(
            color: Colors.white,
            fontFamily: "Poppins",
            fontSize: 24,
          ),
        ),
        backgroundColor: Colors.blue,
        elevation: 0,
      ),
      body: SingleChildScrollView(
        padding: const EdgeInsets.all(16.0),
        child: Column(
          crossAxisAlignment: CrossAxisAlignment.start,
          children: [
            const Text(
              "Hi, I'm Vishnu ðŸ‘‹",
              style: TextStyle(
                fontSize: 28,
                fontWeight: FontWeight.bold,
                fontFamily: "Poppins",
              ),
            ),
            const SizedBox(height: 16),
            const Text(
              "I'm a passionate Flutter Full Stack Developer with experience in building scalable, responsive, and feature-rich applications for mobile and web. I specialize in creating intuitive user interfaces and integrating complex backend functionalities.",
              style: TextStyle(fontSize: 18, fontFamily: "Poppins"),
            ),
            const SizedBox(height: 16),
            const Text(
              "Skills & Expertise:",
              style: TextStyle(
                fontSize: 22,
                fontWeight: FontWeight.bold,
                fontFamily: "Poppins",
              ),
            ),
            const SizedBox(height: 8),
            const Text(
              """
- Flutter Full Stack Development (Mobile & Web)
- Responsive UI Design
- Payment Integration & Push Notifications
- State Management (Provider, Riverpod)
- Firebase Authentication, Firestore, and Realtime Database
- MongoDB & Database Query Optimization
- Node.js for Backend Development
- Map-based Applications (flutter_map)
- Authentication & Authorization
- Version Control with Git/GitHub
              """,
              style: TextStyle(fontSize: 16, fontFamily: "Poppins"),
            ),
            const SizedBox(height: 16),
            const Text(
              "Tools & Technologies:",
              style: TextStyle(
                fontSize: 22,
                fontWeight: FontWeight.bold,
                fontFamily: "Poppins",
