            const SizedBox(height: 8),
            const Text(
              """
- Visual Studio Code
- Firebase Cloud Firestore
- MongoDB
- Dart (Flutter Framework)
- Node.js
              """,
              style: TextStyle(fontSize: 16, fontFamily: "Poppins"),
            ),
            const SizedBox(height: 16),
            const Text(
              "My Journey:",
              style: TextStyle(
                fontSize: 22,
                fontWeight: FontWeight.bold,
                fontFamily: "Poppins",
              ),
            ),
            const SizedBox(height: 8),
            const Text(
              "My journey into software development began with an enthusiasm for problem-solving and creating impactful applications. Over the years, I have honed my skills in full-stack development, focusing on delivering high-quality solutions that meet user needs and business goals.",
              style: TextStyle(fontSize: 16, fontFamily: "Poppins"),
            ),
            const SizedBox(height: 16),
            const Text(
              "Connect with me to explore opportunities to collaborate on exciting projects!",
              style: TextStyle(
                fontSize: 18,
                fontWeight: FontWeight.bold,
                fontFamily: "Poppins",
              ),
              textAlign: TextAlign.center,
            ),
          ],
        ),
      ),
    );
  }
}

void main() {
  runApp(MaterialApp(
    debugShowCheckedModeBanner: false,
    home: AboutPage(),
  ));
}
