import 'package:flutter/material.dart';
import 'Nav.dart';
import 'PhotoSection.dart';
import 'Professional.dart';
import 'footer.dart';

class PortfolioPage extends StatelessWidget {
  const PortfolioPage({super.key});

  @override
  Widget build(BuildContext context) {
    final screenWidth = MediaQuery.of(context).size.width;

    return Scaffold(
      appBar: AppBar(
        backgroundColor: Colors.black,
        elevation: 0,
        title: const Text(
          'Vishnu',
          style: TextStyle(
            fontWeight: FontWeight.bold,
            fontSize: 24,
          ),
        ),
        actions: screenWidth > 800
            ? [
                const NavBarItem(label: 'Home'),
                const NavBarItem(label: 'About'),
                const NavBarItem(label: 'Skills'),
                const NavBarItem(label: 'Testimonials'),
                const NavBarItem(label: 'Blog'),
                TextButton(
                  onPressed: () {},
                  child: const Text(
                    'Contact Me',
                    style: TextStyle(color: Colors.white),
                  ),
                ),
              ]
            : null,
      ),
      drawer: screenWidth <= 800
          ? Drawer(
              child: ListView(
                padding: EdgeInsets.zero,
                children: [
                  const DrawerHeader(
                    decoration: BoxDecoration(color: Colors.black),
                    child: Text(
                      'Vishnu',
                      style: TextStyle(color: Colors.white, fontSize: 24),
                    ),
                  ),
                  ListTile(
                    title: const Text('Home'),
                    onTap: () {},
                  ),
                  ListTile(
                    title: const Text('About'),
                    onTap: () {},
                  ),
                  ListTile(
                    title: const Text('Skills'),
                    onTap: () {},
                  ),
                  ListTile(
                    title: const Text('Testimonials'),
                    onTap: () {},
                  ),
                  ListTile(
                    title: const Text('Blog'),
                    onTap: () {},
                  ),
                ],
              ),
            )
          : null,
      body: const SingleChildScrollView(
        child: Column(
          children: [
            HeroSection(),
            ProfessionalDetails(),
            Footer(),
          ],
        ),
      ),
    );
  }
}
