import 'package:font_awesome_flutter/font_awesome_flutter.dart';
import 'package:flutter/material.dart';

class HeroSection extends StatelessWidget {
  const HeroSection({super.key});

  @override
  Widget build(BuildContext context) {
    final screenWidth = MediaQuery.of(context).size.width;

    return SingleChildScrollView(
      child: Container(
        padding: EdgeInsets.symmetric(
          vertical: screenWidth > 800 ? 50 : 20,
          horizontal: screenWidth > 800 ? 50 : 20,
        ),
        color: Colors.white,
        child: Column(
          crossAxisAlignment: CrossAxisAlignment.start,
          children: [
            Row(
              crossAxisAlignment: CrossAxisAlignment.start,
              children: [
                Flexible(
                  flex: 2,
                  child: Column(
                    crossAxisAlignment: CrossAxisAlignment.start,
                    children: [
                      SizedBox(height: 20),
                      Text(
                        "Hi! I'm Vishnu",
                        style: TextStyle(
                          fontSize: screenWidth > 800 ? 32 : 24,
                          fontWeight: FontWeight.bold,
                        ),
                      ),
                      Text(
                        "Flutter Developer",
                        style: TextStyle(
                          fontSize: screenWidth > 800 ? 32 : 24,
                          fontWeight: FontWeight.bold,
                          color: Colors.blue,
                        ),
                      ),
                      SizedBox(height: 20),
                      Text(
                        "Google Developer Expert for Dart, Flutter & Firebase. Working in Flutter and Firebase for the past 5 years. Let's give your idea a life.",
                        style: TextStyle(
                          fontSize: screenWidth > 800 ? 16 : 14,
                        ),
                      ),
                      SizedBox(height: 20),
                      Row(
                        mainAxisAlignment: screenWidth > 800
                            ? MainAxisAlignment.start
                            : MainAxisAlignment.center,
                        children: screenWidth > 800
                            ? [
                                ElevatedButton(
                                  onPressed: () {},
                                  child: Text("Download CV"),
                                ),
                                SizedBox(width: 10),
                                ElevatedButton(
                                  onPressed: () {},
                                  style: ElevatedButton.styleFrom(
                                    backgroundColor: Colors.black,
                                  ),
                                  child: Text("Hire Me Now"),
                                ),
                              ]
                            : [
                                Flexible(
                                  child: ElevatedButton(
                                    onPressed: () {},
                                    child: Text("Download CV"),
                                  ),
                                ),
                                SizedBox(height: 10),
                                Flexible(
                                  child: ElevatedButton(
                                    onPressed: () {},
                                    style: ElevatedButton.styleFrom(
                                      backgroundColor: Colors.black,
                                    ),
                                    child: Text("Hire Me Now"),
                                  ),
                                ),
                              ],
                      ),
                      SizedBox(height: 20),
                      Row(
                        mainAxisAlignment: MainAxisAlignment.start,
                        children: [
                          IconButton(
                            icon: Icon(FontAwesomeIcons.github),
                            onPressed: () {},
                          ),
                          IconButton(
                            icon: Icon(FontAwesomeIcons.linkedin),
                            onPressed: () {},
                          ),
                          IconButton(
                            icon: Icon(FontAwesomeIcons.twitter),
                            onPressed: () {},
                          ),
                        ],
                      ),
                    ],
                  ),
                ),
                // Image Section (Responsive Circular Image)
                Center(
                  child: Container(
                    width: screenWidth > 800
                        ? screenWidth * 0.25
                        : screenWidth * 0.4, // Adjust radius for mobile
                    height: screenWidth > 800
                        ? screenWidth * 0.25
                        : screenWidth * 0.4, // Adjust radius for mobile
                    decoration: BoxDecoration(
                      color: Colors.grey.shade200,
                      shape: BoxShape.circle,
                      boxShadow: [
                        BoxShadow(
                          color: Colors.black.withOpacity(0.2),
                          blurRadius: 10,
                          spreadRadius: 5,
                        ),
                      ],
                    ),
                    child: ClipOval(
                      child: Image.asset(
                        'assets/snapedit_1735151943920.jpeg-removebg-preview.png',
                        fit: BoxFit.cover,
                      ),
                    ),
                  ),
                ),
              ],
            ),
          ],
        ),
      ),
    );
  }
}
