import 'package:flutter/material.dart';

class SkillsPage extends StatelessWidget {
  final List<String> skills = [
    "Flutter Full Stack Development",
    "Responsive UI",
    "Payment Integration",
    "Push Notifications",
    "Mongo DB",
    "Firebase Cloud Firestore",
    "Dart (Flutter Framework)",
    "Frontend Development",
    "Flutter (Mobile & Web Development)",
    "State Management (Provider, Riverpod)",
    "Firebase Authentication and Integration",
    "flutter_map for map-based applications",
    "Database Management (Firestore & Realtime Database)",
    "Database Query Optimization",
    "Authentication & Authorization",
    "Visual Studio Code",
    "Git/GitHub (Version Control)",
  ];

  @override
  Widget build(BuildContext context) {
    return Scaffold(
      appBar: AppBar(
        title: const Text("My Skills"),
        centerTitle: true,
      ),
      body: LayoutBuilder(
        builder: (context, constraints) {
          bool isMobile = constraints.maxWidth < 600;
          return Padding(
            padding: const EdgeInsets.all(16.0),
            child: GridView.builder(
              gridDelegate: SliverGridDelegateWithFixedCrossAxisCount(
                crossAxisCount: isMobile ? 1 : 2, // Single column on mobile
                crossAxisSpacing: 16,
                mainAxisSpacing: 16,
                childAspectRatio: 4, // Adjust height-to-width ratio
              ),
              itemCount: skills.length,
              itemBuilder: (context, index) {
                return Card(
                  elevation: 4,
                  shape: RoundedRectangleBorder(
                    borderRadius: BorderRadius.circular(12),
                  ),
                  child: Padding(
                    padding: const EdgeInsets.all(12.0),
                    child: Center(
                      child: Text(
                        skills[index],
                        textAlign: TextAlign.center,
                        style: TextStyle(
                          fontSize: isMobile ? 16 : 18,
                          fontWeight: FontWeight.w500,
                        ),
                      ),
                    ),
                  ),
                );
              },
            ),
          );
        },
      ),
    );
  }
}

void main() {
  runApp(MaterialApp(
    debugShowCheckedModeBanner: false,
    home: SkillsPage(),
  ));
}
