// Define a StateNotifier to manage the button's state
class DownloadButtonState extends StateNotifier<bool> {
  DownloadButtonState() : super(false);

  void toggleState() {
    state = !state;
  }
}

// Create a provider for the DownloadButtonState
final downloadButtonProvider =
    StateNotifierProvider<DownloadButtonState, bool>((ref) {
  return DownloadButtonState();
});
